mapper.update:
UPDATE CycleList

	number = number+1,

WHERE id = #{id}
AND `user` = #{user}

UPDATE CycleList

	number = number-1,

WHERE id = #{id}
AND `user` = #{user}

UPDATE ToDoList

	reason = #{reason},

WHERE id = #{id}
AND `user` = #{user}

UPDATE ToDoList

	visible = 0,

WHERE id = #{id}
AND `user` = #{user}

UPDATE ToDoList
success = 0,
WHERE doDate = "0000-00-00 00:00:00" AND `user` = #{user}

UPDATE CycleList

	success = 1,

WHERE id = #{id}
AND `user` = #{user}

mapper.select:
		SELECT *
		FROM ToDoList
       	WHERE doDate = #{doDate}
       	AND user = #{user}
		ORDER BY id DESC

		SELECT *
		FROM ToDoList
       	WHERE success = 0

       	AND doDate > #{start} AND  doDate < #{now}

       	AND `user` = #{user}
		ORDER BY doDate DESC

		SELECT *
		FROM ToDoList
       	WHERE 1

       	AND doDate > #{start} AND  doDate < #{now}

       	AND classification = #{classification}
       	AND `user` = #{user}
		ORDER BY doDate DESC

		SELECT COUNT(*)
		FROM ToDoList
		WHERE 1

       	AND doDate > #{start} AND  doDate < #{now}

       	AND `user` = #{user}
		ORDER BY doDate DESC

mapper.delete:
DELETE FROM CycleList
WHERE id = #{id}
AND `user` = #{user}

mapper.select:
		SELECT *
		FROM ToDoList
       	WHERE doDate = #{doDate}
       	AND `user` = #{user}
		ORDER BY id DESC

		SELECT *
		FROM CycleList
       	WHERE id = #{id}
       	AND `user` = #{user}
